class TokenManager:
    def __init__(self, token: Optional[str] = None):
        self.token = token or os.getenv('API_TOKEN', self._get_default_token())

    @staticmethod
    def _get_default_token() -> str:
        # Replace by your default token
        return "eyJhbGciOiJSUzI1NiIsInR5cCIgOiAiSldUIiwia2lkIiA6ICJqazlObnpLTWVTeTF6Wk53RW1WMHVzY0FFcWFicTY4MGh5ZFpqY2Q0Wl9zIn0..mHPJXRKvXyg9KYHarIrFTBH0R2hmA2svQW3bCu6DMqgkH9S48Bf_27sy8dIybiKME3GNgSfkrS5-bPS5bUeebqq4_IaEIoEc-hZ0zVIANGJjJQamurZUUOkExffjuKcz5B_Jlb4uHUNjT1bsRdL7n9TFjKLwgMtY9ILUf3jQQ_3tqmQ9ia0mmDjsNuxspPmxH7cQMgIz5XXxS6oP4bI1mHAFNC--9IUOnUT4dn5nbqXKOIS5Swn2YnOQeS7HjX78HcvoOgM2lXsSnfW_zhpnfDl4CO4ku2AAGpvTMYwPRlUk3_Rwo0EWa6x9kmLq2WazeyjCR6l2l2c7fngv7L8Dvw"

    def is_valid(self) -> bool:
        if not self.token:
            print("‚ùå Kh√¥ng c√≥ token")
            return False
        try:
            parts = self.token.split('.')
            if len(parts) != 3:
                return False
            payload = json.loads(base64.b64decode(parts[1] + '=='))
            exp_time = payload.get('exp', 0)
            current_time = int(datetime.now().timestamp())
            if current_time > exp_time:
                print(f"‚ùå Token ƒë√£ h·∫øt h·∫°n l√∫c: {datetime.fromtimestamp(exp_time)}")
                return False
            else:
                remaining = exp_time - current_time
                print(f"‚úÖ Token c√≤n hi·ªáu l·ª±c {remaining//3600} gi·ªù {(remaining%3600)//60} ph√∫t")
                return True
        except Exception as e:
            print(f"‚ùå L·ªói ki·ªÉm tra token: {e}")
            return False

    def get_auth_header(self) -> Dict[str, str]:
        return {'Authorization': f'bearer {self.token}'}

class DateTimeUtils:
    @staticmethod
    def format_datetime(date_str: str, is_start: bool = True) -> str:
        if 'T' in date_str:
            return date_str
        if is_start:
            return f"{date_str}T00:00:00.000Z"
        else:
            return f"{date_str}T23:59:59.999Z"

    @staticmethod
    def format_datetime_online(date_str: str, is_start: bool = True) -> str:
        if 'T' in date_str:
            return date_str
        if is_start:
            return f"{date_str}T00:00:00.0Z"
        else:
            return f"{date_str}T23:59:59.0Z"

class HTTPSessionManager:
    @staticmethod
    def create_session() -> requests.Session:
        session = requests.Session()
        retry_strategy = Retry(
            total=APIConfig.MAX_RETRIES,
            status_forcelist=APIConfig.RETRY_STATUS_CODES,
            allowed_methods=["HEAD", "GET", "OPTIONS"],
            backoff_factor=APIConfig.BACKOFF_FACTOR,
            respect_retry_after_header=True
        )
        adapter = HTTPAdapter(
            max_retries=retry_strategy,
            pool_connections=10,
            pool_maxsize=20
        )
        session.mount("http://", adapter)
        session.mount("https://", adapter)
        return session

    @staticmethod
    def get_default_headers() -> Dict[str, str]:
        return {
            'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36',
            'Accept': 'application/json, text/plain, */*',
            'Accept-Encoding': 'gzip, deflate, br',
            'Connection': 'keep-alive'
        }

class APIRequestHandler:
    def __init__(self, token_manager: TokenManager):
        self.token_manager = token_manager
        self.session_manager = HTTPSessionManager()

    def _validate_token(self) -> bool:
        if not self.token_manager.is_valid():
            print("üîÑ Vui l√≤ng c·∫≠p nh·∫≠t token m·ªõi!")
            return False
        return True

    def _make_request(self, url: str, params: Dict[str, Any], description: str) -> Optional[Any]:
        session = self.session_manager.create_session()
        try:
            print(f"‚è≥ ƒêang g·ª≠i request {description}... (c√≥ th·ªÉ m·∫•t v√†i ph√∫t)")
            start_time = time.time()
            # ƒê√¢y l√† ƒëi·ªÉm then ch·ªët: G·ªôp m·ªçi header nh∆∞ code m·∫´u
            headers = {
                **self.token_manager.get_auth_header(),
                'Accept': 'application/json, text/plain, */*',
                'Accept-Language': 'vi',
                'Authorization': self.token_manager.get_auth_header()['Authorization'],  # explicit
                'Content-Type': 'application/json',
                'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) '
                            'AppleWebKit/537.36 (KHTML, like Gecko) '
                            'Chrome/139.0.0.0 Safari/537.36 Edg/139.0.0.0',
            }
           
            response = session.get(
                url,
                headers=headers,
                params=params,
                timeout=(APIConfig.CONNECT_TIMEOUT, APIConfig.READ_TIMEOUT),
                stream=True
            )
            response.raise_for_status()
            elapsed_time = time.time() - start_time
            print(f"‚úÖ Request {description} th√†nh c√¥ng! Th·ªùi gian: {elapsed_time:.2f}s")
            return response.json()
        except requests.RequestException as e:
            elapsed_time = time.time() - start_time
            print(f"‚ùå L·ªói khi g·ªçi {description} sau {elapsed_time:.2f}s: {e}")
            return None
        finally:
            session.close()



    def _handle_http_error(self, error: requests.exceptions.HTTPError):
        status_code = error.response.status_code
        error_messages = {
            401: "üîê L·ªói 401: Token h·∫øt h·∫°n ho·∫∑c kh√¥ng h·ª£p l·ªá",
            403: "üö´ L·ªói 403: Kh√¥ng c√≥ quy·ªÅn truy c·∫≠p",
            429: "üö¶ L·ªói 429: Rate limit - ƒë·ª£i v√† th·ª≠ l·∫°i"
        }
        if status_code in error_messages:
            print(error_messages[status_code])
        elif status_code >= 500:
            print(f"üîß L·ªói server {status_code}: V·∫•n ƒë·ªÅ t·ª´ ph√≠a API")
        else:
            print(f"‚ùå HTTP Error {status_code}: {error}")

class KGGReportHandler(APIRequestHandler):
    def get_report(self, from_date: str, to_date: str, agency_id: str = None) -> Optional[Any]:
        if not self._validate_token():
            return None
        agency_id = agency_id or APIConfig.DEFAULT_AGENCY_ID
        from_date = DateTimeUtils.format_datetime(from_date, True)
        to_date = DateTimeUtils.format_datetime(to_date, False)
        print(f"üìÖ Truy v·∫•n: {from_date} -> {to_date}")
        print(f"üè¢ Agency: {agency_id}")
        params = {
            'agency-id': agency_id,
            'from-date': from_date,
            'to-date': to_date,
            'agencyLevel0': APIConfig.AGENCY_LEVEL_0,
            'agencyLevel1': APIConfig.AGENCY_LEVEL_1,
            'agencyLevel2': APIConfig.AGENCY_LEVEL_2,
            'procedureLevel4': APIConfig.PROCEDURE_LEVEL_4,
            'procedureLevel3': APIConfig.PROCEDURE_LEVEL_3,
            'procedureLevel2': APIConfig.PROCEDURE_LEVEL_2,
            'suppended-cancelled': 'true',
            'hide-agency-no-dossier': 'false',
            'isOnlineAttachResults': 'false',
            'isKGGReportCancel': 'true'
        }
        return self._make_request(APIEndpoints.KGG_STATISTIC.value, params, "KGG Report")

